import React from 'react'

function Text() {
    const client = "Designer"
    const title = {
        graphic: "Graphic",
        missingDe: "Graphic",
        missingPro: "and web design",
        web: "web design"
    }
    return(
        <div>
        <p>        
            
            {client == "Designer" ? <span style={{color: "red"}}>{title.graphic}</span> + <span>and web design</span> : <span style={{color: "cyan"}}><span style={{color: "black"}}> and </span>{title.web}</span>} are two areas that are often confused with each other. After all, they both focus on ‘design’ and they both often work on the same projects. 
        </p>
      </div>
    );
}

export default Text
