import React from 'react'

function Text() {
    const client = "Desinger"

    const title = {
        graphic: <span style={{color: "red"}}>Graphic</span>,
        web: <span style={{color: "red"}}>web design</span>,
        fullText: "Graphic and web design are two areas that are often confused with each other. After all, they both focus on ‘design’ and they both often work on the same projects."
    }
    return(
        <div>
        <p>        
            {client === "Designer" ? title.graphic : client === "Programmer" ? "Graphic" + " " + title.web : title.fullText};
        </p>
      </div>
    );
}

export default Text
